mapper.select:
select board_id, reg_date, sleep_time, wakeup_time, total_time, day_night
from (
	select row_number() over (order by reg_date desc) as seq,
			board_id, reg_date, sleep_time, wakeup_time, total_time, day_night
	from board		
)
where seq between #{start} and #{end}
order by board_id desc

mapper.update:
update board 
set memo = #{memo}
where board_id=#{boardId}

update board 
set wakeup_time = #{wakeupTime}
where board_id=#{boardId}

update board 
set total_time = #{totalTime}
where board_id=#{boardId}
